import React from 'react';
import { Container, Row, Col, Card, Nav } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('isLoggedIn');
    navigate('/admin/login');
  };

  const quickActions = [
    { title: 'Create Bill', icon: 'bi-receipt', description: 'Generate new bill', color: 'primary' },
    { title: 'Manage Inventory', icon: 'bi-box-seam', description: 'View/Update stock', color: 'success' },
    { title: 'Services', icon: 'bi-tools', description: 'Manage services', color: 'info' },
    { title: 'Reports', icon: 'bi-graph-up', description: 'View sales reports', color: 'warning' },
  ];

  return (
    <div className="d-flex">
      

      {/* Main Content */}
      <div className="flex-grow-1 p-4">
        <div className="mb-4">
          <h1>Dashboard</h1>
          <p className="text-muted">Welcome to NSC Billing System</p>
        </div>

        <Row className="g-4">
          {quickActions.map((action, index) => (
            <Col md={6} lg={3} key={index}>
              <Card className="h-100 border-0 shadow-sm">
                <Card.Body className="text-center">
                  <div className={`bg-${action.color} bg-opacity-10 p-3 rounded-circle d-inline-flex mb-3`}>
                    <i className={`${action.icon} text-${action.color} fs-4`}></i>
                  </div>
                  <h5>{action.title}</h5>
                  <p className="text-muted small">{action.description}</p>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>

        <Row className="mt-4">
          <Col md={6}>
            <Card>
              <Card.Header>
                <h5 className="mb-0">Recent Activity</h5>
              </Card.Header>
              <Card.Body>
                <p>Bill #001 - Oil Change Service - $85.00</p>
                <p>Bill #002 - Spark Plugs - $45.00</p>
                <p>Stock Updated - Engine Oil</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={6}>
            <Card>
              <Card.Header>
                <h5 className="mb-0">Quick Stats</h5>
              </Card.Header>
              <Card.Body>
                <p>Today's Sales: $1,250.00</p>
                <p>Bills Today: 8</p>
                <p>Low Stock Items: 3</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Dashboard;
