import React from 'react';
import { Nav } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Sidebar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { logout, user } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/admin/login');
  };

  const menuItems = [
    { path: '/admin/dashboard', icon: 'bi-speedometer2', label: 'Dashboard' },
    { path: '/admin/billing', icon: 'bi-receipt', label: 'Billing' },
    { path: '/admin/inventory', icon: 'bi-box-seam', label: 'Inventory' },
    { path: '/admin/services', icon: 'bi-tools', label: 'Services' },
  ];

  return (
    <div className="sidebar text-white" style={{ width: '250px' }}>
      <div className="p-3 border-bottom">
        <h5 className="mb-0">
          <i className="bi bi-gear-fill me-2"></i>
          NSC Billing
        </h5>
        <small>Staff Portal</small>
        {user && (
          <div className="mt-2">
            <small className="text-muted">Logged in as: {user.username}</small>
            <br />
            <small className="text-muted">Role: {user.role}</small>
          </div>
        )}
      </div>
      
      <Nav className="flex-column p-3">
        {menuItems.map((item) => (
          <Nav.Link
            key={item.path}
            className={`text-white mb-2 rounded ${
              location.pathname === item.path ? 'bg-primary' : 'hover-bg-light'
            }`}
            onClick={() => navigate(item.path)}
            style={{ cursor: 'pointer' }}
          >
            <i className={`${item.icon} me-2`}></i>
            {item.label}
          </Nav.Link>
        ))}
        <Nav.Link 
          className="text-white mb-2 rounded hover-bg-light" 
          onClick={handleLogout}
          style={{ cursor: 'pointer' }}
        >
          <i className="bi bi-box-arrow-right me-2"></i>
          Logout
        </Nav.Link>
      </Nav>
    </div>
  );
};

export default Sidebar;
